<?phpclass Core_Db_Table_Rowset extends Zend_Db_Table_Rowset_Abstract {    /**     * Get options     * @param string $field     * @param string $value     * @return array     */    public function getOptions($field = 'name', $value = 'id') {        $result = array();        foreach ($this as $row) {            if ($row->$field == '' || $row->$value == '') {                continue;            }            $result[] = array(                'name' => $row->$field,                'value' => $row->$value,            );        }        return $result;    }    public function addRow($row) {        if ($row instanceof Core_Db_Table_Row) {            $this->_rows[] = $row;            $this->_data[] = $row->toArray();            $this->_count = count($this->_rows);        } else {        }    }    public function getRowById($id) {        foreach ($this as $row) {            if ($row->id == $id) {                return $row;            }        }        return false;    }}